CREATE TABLE `proveedores` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `nombre` varchar(200) NOT NULL,
  `descripcion` varchar(200) NOT NULL,
  `ruc` varchar(13) NOT NULL,
  `contacto` varchar(200) NOT NULL,
